$(document).ready(function() {
    canSlide = $(".tof-slider-wrapper").attr("data-slide"),
    $(window).width() <= 1e3 ? ($(".tof-slider-wrapper").attr("data-offset", "100"),
    $(".tof-slider-wrapper").attr("data-position", "0"),
    $(".tof-slider-wrapper").attr("data-findex", "false" == canSlide ? "2" : "1"),
    $(".tof-slide").removeAttr("id"),
    $(".tof-slide:nth-child(" + ("false" == canSlide ? "2" : "1") + ")").attr("id", "tof-slide-focus")) : ($(".tof-slider-wrapper").attr("data-offset", "50"),
    $(".tof-slider-wrapper").attr("data-position", "25"),
    $(".tof-slider-wrapper").attr("data-findex", "2"),
    $(".tof-slide").removeAttr("id"),
    $(".tof-slide:nth-child(2)").attr("id", "tof-slide-focus"));
    var t = null
      , e = parseInt($(".tof-slider-wrapper").attr("data-offset"))
      , r = parseInt($(".tof-slider-wrapper").attr("data-position"))
      , a = parseInt($(".tof-slider-wrapper").attr("data-findex"))
      , i = parseInt($(".tof-slider-wrapper").attr("data-findex"))
      , d = $(".tof-slide").length;
    $(window).on("resize", function() {
        var t = $(this);
        t.width() <= 1e3 ? ($(".tof-slider-wrapper").attr("data-offset", "100"),
        $(".tof-slider-wrapper").attr("data-position", "0"),
        $(".tof-slider-wrapper").attr("data-findex", "false" == canSlide ? "2" : "1"),
        $(".tof-slide").removeAttr("id"),
        $(".tof-slide:nth-child(" + ("false" == canSlide ? "2" : "1") + ")").attr("id", "tof-slide-focus"),
        e = parseInt($(".tof-slider-wrapper").attr("data-offset")),
        r = parseInt($(".tof-slider-wrapper").attr("data-position")),
        a = parseInt($(".tof-slider-wrapper").attr("data-findex")),
        i = parseInt($(".tof-slider-wrapper").attr("data-findex")),
        d = $(".tof-slide").length) : ($(".tof-slider-wrapper").attr("data-offset", "50"),
        $(".tof-slider-wrapper").attr("data-position", "25"),
        $(".tof-slider-wrapper").attr("data-findex", "2"),
        $(".tof-slide").removeAttr("id"),
        $(".tof-slide:nth-child(2)").attr("id", "tof-slide-focus"),
        e = parseInt($(".tof-slider-wrapper").attr("data-offset")),
        r = parseInt($(".tof-slider-wrapper").attr("data-position")),
        a = parseInt($(".tof-slider-wrapper").attr("data-findex")),
        i = parseInt($(".tof-slider-wrapper").attr("data-findex")),
        d = $(".tof-slide").length)
    }),
    "true" == canSlide ? ($(".tof-slider-button").removeClass("tof-slider-button-hide"),
    $(".tof-slider-next").on("click", function(t) {
        t.stopPropagation(),
        r += e;
        var a = -1 * r;
        $(".tof-slider-wrapper").css("transform", "translateX(" + a + "%)"),
        i += 1;
        var o = parseInt($(".tof-slide").last().attr("class").split(" ")[1].split("_")[1])
          , s = 1;
        s = o + 1 > d ? 1 : o + 1;
        var f = $(".tof-slide:nth-child(" + s + ")").clone();
        $(".tof-slider-wrapper").append(f),
        $(".tof-slide").removeAttr("id"),
        $(".tof-slide:nth-child(" + i + ")").attr("id", "tof-slide-focus")
    }),
    $(".tof-slider-back").on("click", function(t) {
        t.stopPropagation(),
        i != a && (i -= 1,
        r -= e,
        $(".tof-slider-wrapper").css("transform", "translateX(-" + r + "%)"),
        $(".tof-slide").removeAttr("id"),
        $(".tof-slide:nth-child(" + i + ")").attr("id", "tof-slide-focus"))
    })) : ($("body").css("--tof-slider-mob-default-pos", "-100%"),
    $(".tof-slider-button").addClass("tof-slider-button-hide")),
    $(".tof-slider-next").on("click", function(t) {
        t.stopPropagation()
    }),
    t = setInterval(function() {
        $(".tof-slider-next").trigger("click")
    }, 5e3),
    $(".tof-slider").on({
        mouseenter: function() {
            clearInterval(t)
        },
        mouseleave: function() {
            t = setInterval(function() {
                $(".tof-slider-next").trigger("click")
            }, 5e3)
        }
    }),
    $(window).on("resize", function() {
        $(".tof-slider-next").trigger("click")
    })
});