$(document).ready(function() {
    $(".tof-donate-input-wrapper").on("click", function() {
        $(this).find("input").focus()
    }),
    $(document).on("click", function() {
        $(".tof-donate-button").removeClass("active")
    }),
    $(".tof-donate-input").on("input", function() {
        this.value = this.value.replace(/[^0-9]/g, "")
    }),
    $(".tof-donate-button").on("click", function(t) {
        t.stopPropagation(),
        $(this).addClass("active");
        var n = $(".tof-donate-input").val().trim();
        "" != n && (window.location.href = "/donate?amount=" + n)
    })
});